sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
	var futureTime = new GlideDateTime();
	var gTime = new GlideTime();
	gTime.setValue("24:00:00");
	futureTime.add(gTime);

	// Query the database for NeedIt Task records with Due date field values older
	// than the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var upcomingNITasks = new GlideRecord('x_58872_needit_needit_task');
	upcomingNITasks.addQuery('due_date','>=',rightNow);
	upcomingNITasks.addQuery('due_date','<=',futureTime);
	upcomingNITasks.addQuery('state','!=',3);

	upcomingNITasks.query();
	// Write a log message for each overdue NeedIt Task Record
	while(upcomingNITasks.next()){
		gs.info("Upcoming NeedIt Task record = " + upcomingNITasks.number);
		gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',upcomingNITasks,upcomingNITasks.number,gs.getUserName());
	}
